// -------------------------------// Kevin T. Manley// CSE 593// -------------------------------package ResImpl;public class Flight extends ReservableItem{		public Flight( int flightNum, int flightSeats, int flightPrice )		{			super(Integer.toString(flightNum), flightSeats, flightPrice );		}		public String getKey()		{			return Flight.getKey( Integer.parseInt( getLocation() ) );		}		public static String getKey( int flightNum )		{			String s = "flight-" + flightNum;			return s.toLowerCase();		}	@Override	public RMItem makeACopy() {		return new Flight(Integer.valueOf(this.m_strLocation), this.m_nCount, this.m_nPrice);	}//		public static String getNumReservationsKey( int flightNum )//		{//			String s = "flight-" + flightNum + "-reservations";//			return s.toLowerCase();//		}}